import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { Link, Navigate } from 'react-router-dom';

function Main() {



    const [id, setId] = useState();
    const [name, setName] = useState();
    const [age, setAge] = useState();
    const [level, setLevel] = useState();
    const [gpa, setGpa] = useState();
    const [post,setPost] =useState()
    const display=(e)=>{
        var agent;
        for(var i=0;i<2;i++)
        {
        agent=e[i].name+" "+agent;
        }
        return agent;
    }
    return (

        <div>
            <h1>Add new student</h1>
            <div className="add">

                Name<input type={'text'} onChange={e => setName(e.target.value)}></input>
                Age<input type={'number'} onChange={e => setAge(e.target.value)}></input>
                Level<input type={'text'} onChange={e => setLevel(e.target.value)}></input>
                Gpa<input type={'text'} onChange={e => setGpa(e.target.value)}></input>
                <button onClick={() => {
                    axios.post('http://localhost:8080/main/user/add', {
                        name: name,
                        age: age,
                        level: level,
                        gpa: gpa
                    }).then(function (response) {
                        // handle success
                        console.log(response);

                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Add new student</button>
            </div >
            <h1>Delete student</h1>
            <div className="delete">
                Id<input type={'number'} onChange={e => setId(e.target.value)}></input>
                <button onClick={() => {
                    var val="http://localhost:8080/main/user/delid/"+id
                    axios.delete(val, {

                    }).then(function (response) {
                        // handle success
                        console.log(response);

                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Delete</button>
            </div>
            <h1>Update student information</h1>
            <div className="update">
                Id<input type={'number'} onChange={e => setId(e.target.value)}></input>
                Name<input type={'text'} onChange={e => setName(e.target.value)}></input>
                Age<input type={'number'} onChange={e => setAge(e.target.value)}></input>
                Level<input type={'text'} onChange={e => setLevel(e.target.value)}></input>
                Gpa<input type={'text'} onChange={e => setGpa(e.target.value)}></input>
                <button onClick={() => {

                    axios.put("http://localhost:8080/main/user/put", {
                        id:id,
                        name: name,
                        age: age,
                        level: level,
                        gpa: gpa

                    }).then(function (response) {
                        // handle success
                        console.log(response);

                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Update</button>

            </div>

            <h1>Serach student information</h1>
            <div className="search">
            Id<input type={'number'} onChange={e => setId(e.target.value)}></input>   
            <button onClick={() => {
                    var val="http://localhost:8080/main/user/getid/"+id
                    axios.get(val, {

                    }).then(function (response) {
                        // handle success
                        console.log(response);

                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Serach</button>
            </div>

            <h1>All student information</h1>
            <div className="all">
                please click to refresh<button onClick={() => {

                    axios.get("http://localhost:8080/main/user/all", {

                    }).then(function (response) {
                        // handle success
                        console.log(response);
                        setPost(response.data)
                        console.log(post)
                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Serach All</button>
            </div>
            <div>

               {display(post)}
            </div>
        </div>
    )
}
export default Main;
