import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { Link,useNavigate} from 'react-router-dom';


function Signup() {
    let navigate = useNavigate();
    const signup = (prop) => {

        if (prop) {
            alert("Register successfull");
            navigate('/')
        }
        else {
            alert("Fail, please try a new account name");
        }

    }
    const [accountUp, setAccountUp] = useState();
    const [passwordUp, setPasswordUp] = useState();
    return (
        <div className="signupPage">
            <center>
                <div>
                    <h1>Hi! welcome to use student information system</h1>
                    <h1>Please register below</h1>
                </div>
            <div>
                Account <input type={"text"} onChange={e => setAccountUp(e.target.value)} ></input>

            </div>
            <div>
                Password<input type={"password"} onChange={e => setPasswordUp(e.target.value)}></input>
            </div>
            <button onClick={() => {
                axios.post('http://localhost:8080/login/signup', {
                    account: accountUp,
                    password: passwordUp
                }).then(function (response) {
                    // handle success
                    console.log(response);
                    signup(response.data)
                })
                    .catch(function (error) {
                        // handle error
                        console.log(error);
                    })
            }}>Sign Up</button>
            </center>
        </div>
    )
}
export default Signup
