import React from "react";

import axios from "axios";


const account=function()
{
    return(
        <div>
            Account <input type={"text"} className="signUpAccount"></input>
        </div>
    )
}
const password=function()
{
    return(
        <div>
            Password<input type={"password"} className="signUpPassword"></input>
        </div>
    )
}
var display;

function Login() {
    return (
        <div className="welcomeTitle">
            <center>
                <br />
                <div >
                    Welcome to My todo list
                </div>
                <br />
                <div>
                    Please log in.
                </div>
                <br />
                <div>
                    <account/>
                </div>
                <div>
                    <password/>
                </div>
                <br />
                <button onClick={() => {
                    axios.get('http://localhost:8080/login/check', {
                        "account": "collin",
                        "password": "19971225"
                    })
                }}>Sign In</button>


                <br />
                <button onClick={() => {
                    axios.get("http://localhost:8080/login/").then((res) => {
                        display=res.data
                        console.log(res)
                        console.log(display)
                    })
                }}>List</button>
                <br />
                if you are new user, you can sigup


                <div>
                     <account/>

                </div>
                <div>
                    <password/>
                </div>
                <button onClick={() => {
                    axios.post('http://localhost:8080/login/signup', {
                        account: {account},
                        password: {password}
                    }).then(function (response) {
                        // handle success
                        console.log(response);
                      })
                      .catch(function (error) {
                        // handle error
                        console.log(error);
                      })
                }}>Sign Up</button>
            </center>
            <div>
                <p>{display}</p>
            </div>
        </div>
    );
}

export default Login;
