import * as React from 'react';
import { useState } from "react";
import axios from "axios";
import { Link,useNavigate} from 'react-router-dom';
import Button from '@mui/material/Button';
import { TextField } from '@mui/material';





export default function Login() {
    let navigate = useNavigate();
    const signin = (prop) => {

        if (prop === "Success") {
            alert("Login Success!");
            navigate('/Main')
        }
        else {
            navigate('/')
            alert(prop);
        }
    }

    const [accountIn, setAccountIn] = useState();
    const [passwordIn, setPasswordIn] = useState();

    //const [login,setLogIn]=useState();
    return (
        <div className="welcomeTitle">
            <center>
                <br />
                <div >
                    Student Information System
                </div>
                <br/>
                <div>
                    Welcome Admin
                </div>
                <br />
                <div>
                    Please log in.
                </div>
                <br />
                <center>
                     <TextField variant="outlined" size='small' label='account' onChange={e => setAccountIn(e.target.value)}></TextField>
                </center>
                <center>
                Password<TextField variant="outlined" size='small' type={"password"} onChange={e => setPasswordIn(e.target.value)}></TextField>
                </center>
                <br />

                <Button size='small' variant="contained" onClick={() => {
                    axios.post('http://localhost:8080/login/check', {
                        account: accountIn,
                        password: passwordIn
                    }).then(function (response) {
                        // handle success
                        console.log(response);
                        signin(response.data);
                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Sign In</Button>

                <Button variant="contained" size='small' onClick={()=>{navigate('/signup')}}>Sign Up</Button>
            </center>


            <Button color="secondary">Secondary</Button>
<Button variant="contained" color="success">
  Success
</Button>
<Button variant="outlined" color="error">
  Error
</Button>

        </div>
    );
}
