import React from "react";

import axios from "axios";



function Login() {
    const [account, setAccount] = useState('');
    const [password,setPassword]= useState('');
    return (
        <div className="welcomeTitle">
            <center>
                <br />
                <div >
                    Welcome to My todo list
                </div>
                <br />
                <div>
                    Please log in.
                </div>
                <br />
                <div>
                    Account <input type={"text"} onChange={this.setAccount}></input>
                </div>
                <div>
                    Password<input type={"password"} onChange={this.setPassword}></input>
                </div>
                <br />
                <button onClick={() => {
                    axios.get('http://localhost:8080/login/check', {
                        "account":"d" ,
                        "password": "19971225"
                    }).then((res) => {

                        console.log(res)

                    }).catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Sign In</button>


                <br />
                <button onClick={() => {
                    axios.get("http://localhost:8080/login", {
                    }).then((res) => {

                        console.log(res)

                    }).catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>List</button>
                <br />
                if you are new user, you can sigup


                <div>
                    Account <input  type={"text"} onChange={this.setAccount}></input>

                </div>
                <div>
                    Password<input  type={"password"} onChange={this.setAccount}></input>
                </div>
                <button onClick={() => {
                    axios.post('http://localhost:8080/login/signup', {
                        account: {account},
                        password: {password}
                    }).then(function (response) {
                        // handle success
                        console.log(response);
                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Sign Up</button>
            </center>
            <div>
                <p>{account}</p>
            </div>
        </div>
    );
}

export default Login;
