import * as React from 'react';
import { useState } from "react";
import axios from "axios";
import { Link,useNavigate} from 'react-router-dom';
import Button from '@mui/material/Button';





export default function Login() {
    let navigate = useNavigate();
    const signin = (prop) => {

        if (prop === "Success") {
            alert("Login Success!");
            navigate('/Main')
        }
        else {
            navigate('/')
            alert(prop);
        }
    }

    const [accountIn, setAccountIn] = useState();
    const [passwordIn, setPasswordIn] = useState();

    //const [login,setLogIn]=useState();
    return (
        <div className="welcomeTitle">
            <center>
                <br />
                <div >
                    Student Information System
                </div>
                <br/>
                <div>
                    Welcome Admin
                </div>
                <br />
                <div>
                    Please log in.
                </div>
                <br />
                <div>
                    Account <input type={"text"} onChange={e => setAccountIn(e.target.value)}></input>
                </div>
                <div>
                    Password<input type={"password"} onChange={e => setPasswordIn(e.target.value)}></input>
                </div>
                <br />

                <button variant="contained" onClick={() => {
                    axios.post('http://localhost:8080/login/check', {
                        account: accountIn,
                        password: passwordIn
                    }).then(function (response) {
                        // handle success
                        console.log(response);
                        signin(response.data);
                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Sign In</button>










                <Link to='/Signup'>
                <button >Sign Up</button></Link>
            </center>

            <Button variant="contained">你好，世界</Button>


        </div>
    );
}
