import React from "react";
import { useState } from "react";
import axios from "axios";
import { Link,useNavigate} from 'react-router-dom';





export default function Login() {
    let navigate = useNavigate();
    const signin = (prop) => {

        if (prop === "Success") {
            alert("Login Success!");
            navigate('/Main')
        }
        else {
            navigate('/')
            alert(prop);
        }
    }
    const signup = (prop) => {

        if (prop) {
            alert("Register successfull");
        }
        else {
            alert("Fail, please try a new account name");
        }

    }
    const [accountIn, setAccountIn] = useState();
    const [passwordIn, setPasswordIn] = useState();
    const [accountUp, setAccountUp] = useState();
    const [passwordUp, setPasswordUp] = useState();
    //const [login,setLogIn]=useState();
    return (
        <div className="welcomeTitle">
            <center>
                <br />
                <div >
                    Student Information System
                </div>
                <br/>
                <div>
                    Welcome Admin
                </div>
                <br />
                <div>
                    Please log in.
                </div>
                <br />
                <div>
                    Account <input type={"text"} onChange={e => setAccountIn(e.target.value)}></input>
                </div>
                <div>
                    Password<input type={"password"} onChange={e => setPasswordIn(e.target.value)}></input>
                </div>
                <br />

                <button onClick={() => {
                    axios.post('http://localhost:8080/login/check', {
                        account: accountIn,
                        password: passwordIn
                    }).then(function (response) {
                        // handle success
                        console.log(response);
                        signin(response.data);
                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Sign In</button>



                <br />

                <Link to='/Main'>aaa</Link>
                <br />
                if you are new user, you can sigup


                <div>
                    Account <input type={"text"} onChange={e => setAccountUp(e.target.value)} ></input>

                </div>
                <div>
                    Password<input type={"password"} onChange={e => setPasswordUp(e.target.value)}></input>
                </div>
                <button onClick={() => {
                    axios.post('http://localhost:8080/login/signup', {
                        account: accountUp,
                        password: passwordUp
                    }).then(function (response) {
                        // handle success
                        console.log(response);
                         signup(response.data) ;
                    })
                        .catch(function (error) {
                            // handle error
                            console.log(error);
                        })
                }}>Sign Up</button>
            </center>
            {/*<div>
                <p>this is {accountIn}</p>
                <p>{passwordIn}</p>
            </div>
            */}
            <div>

            </div>
        </div>
    );
}
